// list of users

import React, { Component } from "react";
import { Table, Button, Image, List } from "semantic-ui-react";
// import UserListRow from "../components/UserListRow";
import "./UserList.css";
import Cat from "../images/cat.jpg";
import VotePopup from "./VotePopup";

class UserList extends Component {

  constructor(props) {
    super(props);
    this.state = {
        showPopup: false,
        voteFor: "",
    };

    this.handlePopUp = this.handlePopUp.bind(this);
  }



  // add to voted
  // toggle popup
  handlePopUp = (voteForKey) => {
    console.log(voteForKey);
    // console.log(this.props.users[voteForKey].name)
    this.setState({
        showPopup: !this.state.showPopup, 
        voteFor: this.props.users[voteForKey].name
    })

  };

  // confirm vote 
  handleConfirmVote = () => {

  }



  render() {
    return (
        <div>


      <div className="UserList">
        <List divided verticalAlign="middle">
          {Object.keys(this.props.users).map((keyName, i) => (
            <List.Item className={keyName}>
              <List.Content floated="right">
                <Button onClick={() => this.handlePopUp(keyName)}>Vote</Button>
              </List.Content>
              <Image avatar src={Cat} />
              <List.Content> {this.props.users[keyName].name} </List.Content>
            </List.Item>
          ))}
        </List>


      </div>
      {this.state.showPopup ? <VotePopup handlePopup={this.handlePopUp}> </VotePopup> : null}
      </div>
    );
  }
}

export default UserList;
