// list of users

import React, {Component} from 'react';
import { Table, Button, Image, List } from 'semantic-ui-react';
import UserListRow from '../components/UserListRow';
import './UserList.css';


class UserList extends Component{

    constructor(props){
        super(props);
    };

    // change color
    // add to voted 
    handleClick = () => {
        var color = this.state.clicked ? 'white' : 'green';
        this.setState({ bgColor: color, clicked: !this.state.clicked})
    }

    render(){
        return(

            <div className='UserList'>

<List divided verticalAlign='middle'>
    <List.Item>
      <List.Content floated='right'>
        <Button>Add</Button>
      </List.Content>
      <Image avatar src='../images/cat.jpg' />
      <List.Content>Lena</List.Content>
    </List.Item>
    <List.Item>
      <List.Content floated='right'>
        <Button>Add</Button>
      </List.Content>
      <Image avatar src='/images/avatar/small/lindsay.png' />
      <List.Content>Lindsay</List.Content>
    </List.Item>
    <List.Item>
      <List.Content floated='right'>
        <Button>Add</Button>
      </List.Content>
      <Image avatar src='/images/avatar/small/mark.png' />
      <List.Content>Mark</List.Content>
    </List.Item>
    <List.Item>
      <List.Content floated='right'>
        <Button>Add</Button>
      </List.Content>
      <Image avatar src='/images/avatar/small/molly.png' />
      <List.Content>Molly</List.Content>
    </List.Item>
  </List>

            </div>




        );
    }





};

export default UserList;
